export function newWebRTCClient() { 
    var conn = new connHandler()

    conn.negotiate()
}

const defaultIceServers = {
    iceServers: [
        {
            urls: 'stun:stun.l.google.com:19302'
        }
    ]
}

class connHandler {
    constructor() {
        this.socket = new WebSocket("ws://" + window.location.hostname + ":8050/ws")
        this.peerConnection = new RTCPeerConnection(defaultIceServers)
        this.listener = null
    }

    negotiate() {
        this.peerConnection.createOffer()
            .then(d => this.peerConnection.setLocalDescription(d))
            .then(this.receiveOffer())
            .then(this.addUserMedia())
            .then(this.sendIceCandidates())
            .then(this.receiveIceCandidates())
    }

    receiveOffer() {
        console.log("receiveOffer")
        this.listener = this.newListener()

        if ( this.listener().sdp ) {
            this.peerConnection.setRemoteDescription(new RTCSessionDescription(this.listener()), function () {
                if (this.peerConnection.remoteDescription.type == "offer") {
                    this.peerConnection.createAnswer().then(function(answer) {
                        this.send(answer);
                        return this.peerConnection.setLocalDescription(answer);
                    })
                    .catch(e => console.log(e));
                }
            }, e => console.log(e))
        } 
    }

    newListener() {
        console.log("newListener")
        return this.socket.onmessage = function(event) {
            var message = JSON.parse(event.data)
            return message
        }
    }

    addUserMedia() {
        console.log("addUserMedia")
        async function startStream() {
            this.video = await navigator.mediaDevices.getUserMedia({ video: true })
        }
        startStream()
    }

    sendIceCandidates() {
        console.log("sendIceCandidate")
        return this.peerConnection.onicecandidate = event => {
            if ( isOpen(this.socket) && event.candidate != null) {
                this.send(this.socket, event.candidate)
            }}
    }

    send(payload) {
        console.log("send: ", payload)
        this.socket.send(JSON.stringify(payload))
    }



    listen() {
        return this.socket.onmessage = function (evt) {
            try {
                var message = JSON.parse(evt.data);
                console.log("receiving: ", message)
                if (message.sdp) {

                } else {
                    this.peerConnection.addIceCandidate(message)
                        .then(console.log("candidate added"))
                        .catch( e => console.log(e))
                }
            } catch(e) {
                console.log("error: ", e)
                console.log("message: ", )
            }

        }
    }
}

function isOpen(socket) {
    return (socket.readyState == WebSocket.OPEN)
}
