export function newWebRTCClient() { 
    var conn = new connHandler()

    conn.negotiate()
}

const defaultIceServers = {
    iceServers: [
        {
            urls: 'stun:stun.l.google.com:19302'
        }
    ]
}

class connHandler {
    constructor() {
        this.socket = new WebSocket("ws://" + window.location.hostname + ":8050/ws")
        this.peerConnection = new RTCPeerConnection(defaultIceServers)
        this.listener = null
        this.start = async function startStream() {
            this.video = await navigator.mediaDevices.getUserMedia({ video: true })
        }

    }

    negotiate() {
        this.peerConnection.createOffer()
            .then(d => this.peerConnection.setLocalDescription(d))
            this.startListener()
            this.addUserMedia()
            this.sendIceCandidates()
            // this.receiveIceCandidates()
    }

    startListener() {
        console.log("startListener")
        this.listener = this.newListener()
    }

    newListener() {
        console.log("newListener")
        return this.socket.onmessage = function(event) {

            var message = JSON.parse(event.data)
            console.log(message, message.type)
            console.log("spd: ", message.spd)
            if (message.spd) {
                console.log("true")
                this.receiveOffer(message)
            }
        }
    }

    receiveOffer(message) {
        console.log("receoveOffer")
        this.peerConnection.setRemoteDescription(new RTCSessionDescription(message), function () {
            if ( this.isOffer() ) {
                this.sendAnswer()
            }
        }, e => console.log(e))
    }

    sendAnswer() {
        console.log("sendAnswer")
        this.peerConnection.createAnswer()
            .then(function(answer) {
                this.send(answer)
                return this.peerConnection.setLocalDescription(answer)
            })
    }

    addUserMedia() {
        console.log("addUserMedia")

        this.start()
    }

    sendIceCandidates() {
        console.log("sendIceCandidate")
        return this.peerConnection.onicecandidate = event => {
            if ( this.isOpen() && event.candidate != null) {
                this.send(this.socket, event.candidate)
            }}
    }

    send(payload) {
        console.log("send: ", payload)
        this.socket.send(JSON.stringify(payload))
    }



    listen() {
        return this.socket.onmessage = function (evt) {
            try {
                var message = JSON.parse(evt.data);
                console.log("receiving: ", message)
                if (message.sdp) {

                } else {
                    this.peerConnection.addIceCandidate(message)
                        .then(console.log("candidate added"))
                        .catch( e => console.log(e))
                }
            } catch(e) {
                console.log("error: ", e)
                console.log("message: ", )
            }

        }
    }

    isOpen() {
        return ( this.socket.readyState == WebSocket.OPEN )
    }

    isOffer() {
        return ( this.peerConnection.remoteDescription.type == "offer" )
    }

    isAnswer() {
        return ( this.peerConnection.remoteDescription.type == "answer" )
    }
}
