export function startConnection(stream) {
    let pc = new RTCPeerConnection({
        iceServers: [
            {
                urls: 'stun:stun.l.google.com:19302'

            }
        ]
    })

    var socket = new WebSocket("ws://" + window.location.hostname + ":8050/ws");

    pc.addStream(stream)
    pc.createOffer().then(d => pc.setLocalDescription(d))

    pc.onicecandidate = event => {
        if ( isOpen(socket) && event.candidate != null) {
            send(socket, event.candidate)
        }
    };

    receive(socket, pc)
}

function isOpen(socket) {
    return (socket.readyState == WebSocket.OPEN)
}

function send(socket, data) {
    socket.send(JSON.stringify(data))
}

function receive(socket, pc) {
    return socket.onmessage = receivedString => {
        console.log("string: ", receivedString.data)
        console.log("stringified: ", JSON.stringify(receivedString.data))
        const message = JSON.parse(receivedString.data);
        console.log("message: ", message);

        console.log(message.ice)
        if (message.ice) {     
            pc.addIceCandidate(message.ice).catch(e => {
                console.log("ICE TRUE Failure during addIceCandidate(): " + e.name);
            });
        }
        pc.addIceCandidate(message.ice).catch(e => {
            console.log("ICE FALSEFailure during addIceCandidate(): " + e.name);
        });
    }
}
